import React from 'react';
import '../style/DriverCard.css';

const DriverCard = ({ driver }) => {
    return (
        <div className="driver-card">
            <h2>{driver.name}</h2>
            <p>Vehicle: {driver.vehicle.registration}</p>
            <p>Total Minutes: {driver.totalMinutes}</p>
            <div className="activity-types">
                {Object.keys(driver.activityTypes).map(type => (
                    <p key={type}>{type}: {driver.activityTypes[type]}</p>
                ))}
            </div>
            <div className="week">
                {driver.week.map((day, index) => (
                    <div key={index} className={`day ${day.hasActivity ? 'active' : ''}`}></div>
                ))}
            </div>
        </div>
    );
};

export default DriverCard;
