import React from 'react';

const DriverItem = ({ driver }) => {
const totalMinutes = driver.activities.reduce((sum, activity)=> sum + activity.minutes, 0);

const activityDays = ['1/2/2021', '2/2/2021', '3/2/2021',
'4/2/2021', '5/2/2021', '6/2/2021', '7/2/2021'];

 return (
<div className="driver-item">
 <h3>{driver.name}</h3>
 <p>Registration: {driver.registration}</p>
 <p>Total Minutes: {totalMinutes}</p>
 <div className="days">
 {activityDays.map(day => (
 <div
 key={day}
 className={`day ${driver.activities.some(activity =>
activity.date === day) ? 'active' : ''}`}
 >
 {day}
 </div>
 ))}
 </div>
 </div>
 );
 };

 export default DriverItem;
