import React, { useEffect, useState } from 'react';
import '../style/DriverList.css';

const DriverList = ({ searchQuery }) => {
    const [drivers, setDrivers] = useState([]);

    useEffect(() => {
        fetch('/data/drivers.json')
            .then(response => response.json())
            .then(data => setDrivers(data));
    }, []);

    const filteredDrivers = drivers.filter(driver =>
        driver.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        driver.vehicle.registration.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="driver-list">
            {filteredDrivers.map(driver => (
                <div key={driver.id} className="driver-card">
                    <h2>{driver.name}</h2>
                    <p>Vehicle: {driver.vehicle.registration}</p>
                    <p>Total Minutes: {driver.totalMinutes}</p>
                    <div className="activities">
                        {Object.entries(driver.activities).map(([type, time]) => (
                            <p key={type}>{type}: {time} mins</p>
                        ))}
                    </div>
                    <div className="week">
                        {driver.week.map((day, index) => (
                            <div key={index} className={`day ${day.hasActivity ? 'active' : ''}`}></div>
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
};

export default DriverList;
