// src/components/DriverList.js
import React from 'react';
import driversData from '../data/drivers.json';

const DriverList = () => {
  const calculateTotalTime = activities =>
    activities.reduce((total, activity) => total + activity.duration, 0);

  return (
    <div>
      {driversData.map(driver => (
        <div key={driver.driver_id}>
          <span>{driver.name}</span>
          <span>{driver.vehicle_reg}</span>
          <span>{calculateTotalTime(driver.activities)}</span>
          {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map(day => (
            <span
              key={day}
              style={{ backgroundColor: driver.activities.some(a => a.date.includes(day)) ? 'green' : 'transparent' }}
            >
              {day}
            </span>
          ))}
        </div>
      ))}
    </div>
  );
};

export default DriverList;
